#include <bits/stdc++.h>
typedef long long ll;

const ll INF = 9'000'000'000'000'000'000;

template<typename T>
bool assign_max(T& a, T b) {
	if (b > a) {
		a = b;
		return true;
	}
	return false;
}

template<typename T>
bool assign_min(T& a, T b) {
	if (b < a) {
		a = b;
		return true;
	}
	return false;
}

using namespace std;

struct DSU {
	vector<ll> parent;
	ll col;
	DSU(ll n) {
		col = n;
		parent.resize(n, 0);
		for (ll i = 0; i < n; i++) {
			parent[i] = i;
		}
	}
	ll get(ll a) {
		return (parent[a] == a ? a : parent[a] = get(parent[a]));
	}
	void unite(ll a, ll b) {
		a = get(a);
		b = get(b);
		parent[b] = a;
		col -= b != a;
	}
};

int main() {
    	ios_base::sync_with_stdio(false);
    	cin.tie(0);
    	cout.tie(0);
	ll n, m;
	cin >> n >> m;
	DSU d(n);
	vector<bitset<1000>> arr(n);
	bitset<1000> zero;
	for (ll i = 0; i < m; i++) {
		ll a, b;
		cin >> a >> b;
		a--;
		b--;
		d.unite(a, b);
		arr[a][b] = true;
		arr[b][a] = true;
	}
	if (d.col > 2) {
		cout << "No";
		return 0;
	}
	for (ll i = 0; i < n; i++) {
		arr[i][i] = true;
	}
	bool b = true;
	vector<ll> color(n, 0);
	for (ll i = 0; i < n; i++) {
		for (ll j = i + 1; j < n; j++) {
			if (((arr[i] & arr[j]) != zero) && (arr[i] != arr[j])) {
				b = false;
				break;
			}
		}
		if (!b) {
			break;
		}
	}
	cout << (b ? "Yes" : "No");
}
