#include <bits/stdc++.h>
typedef long long ll;

const ll INF = 9'000'000'000'000'000'000;

template<typename T>
bool assign_max(T& a, T b) {
	if (b > a) {
		a = b;
		return true;
	}
	return false;
}

template<typename T>
bool assign_min(T& a, T b) {
	if (b < a) {
		a = b;
		return true;
	}
	return false;
}

using namespace std;

struct DSU {
	vector<ll> parent, k;
	ll col;
	DSU(ll n) {
		col = 0;
		parent.resize(n, 0);
		k.resize(n, 0);
		for (ll i = 0; i < n; i++) {
			parent[i] = i;
		}
	}
	ll get(ll a) {
		return (parent[a] == a ? a : parent[a] = get(parent[a]));
	}
	void unite(ll a, ll b) {
		a = get(a);
		b = get(b);
		if (a == b) {
			return;
		}
		if (k[a] < k[b]) {
			swap(a, b);
		}
		parent[b] = a;
		col--;
		k[a] += k[b];
	}
};

int main() {
    	ios_base::sync_with_stdio(false);
    	cin.tie(0);
    	cout.tie(0);
	ll n, m;
	cin >> n >> m;
	vector<vector<ll>> arr(n, vector<ll>(m, 0));
	for (ll i = 0; i < n; i++) {
		for (ll j = 0; j < m; j++) {
			char c;
			cin >> c;
			arr[i][j] = c - '0';
		}
	}
	ll ans = 0;
	for (ll i = 0; i < n; i++) {
		DSU d(n * m);
		for (ll j = i; j < n; j++) {
			for (ll k = 0; k < m; k++) {
				if (arr[j][k] == 0) {
					d.col--;
				} else {
					if (k != 0) {
						if (arr[j][k - 1] == 1) {
							d.unite(j * m + k, j * m + k - 1);
						}
					}
					if (i != j) {
						if (arr[j - 1][k] == 1) {
							d.unite(j * m + k, j * m - m + k);
						}
					}
				}
			}
			ans += (j - i + 1) * m + d.col;
		}
	}
	cout << ans;
}
