#include <bits/stdc++.h>
typedef long long ll;

const ll INF = 9'000'000'000'000'000'000, mod = 1'000'000'007;

template<typename T>
bool assign_max(T& a, T b) {
	if (b > a) {
		a = b;
		return true;
	}
	return false;
}

template<typename T>
bool assign_min(T& a, T b) {
	if (b < a) {
		a = b;
		return true;
	}
	return false;
}

using namespace std;

int main() {
    	ios_base::sync_with_stdio(false);
    	cin.tie(0);
    	cout.tie(0);
	ll n, m;
	cin >> n >> m;
	vector<vector<ll>> arr(n, vector<ll>(m));
	for (ll i = 0; i < n; i++) {
		for (ll j = 0; j < m; j++) {
			cin >> arr[i][j];
		}
	}
	ll q;
	cin >> q;
	if (m <= 300) {
		for (ll i = 0; i < q; i++) {
			ll x, y;
			cin >> x >> y;
			x--;
			y--;
			for (ll j = 0; j < m; j++) {
				arr[x][j] = (arr[x][j] + arr[y][j]) % mod;
			}
		}
		for (auto i : arr) {
			for (auto j : i) {
				cout << j << ' ';
			}
			cout << '\n';
		}
	} else {
		vector<vector<ll>> col(n, vector<ll>(n, 0));
		for (ll i = 0; i < n; i++) {
			col[i][i] = 1;
		}
		for (ll i = 0; i < q; i++) {
			ll x, y;
			cin >> x >> y;
			x--;
			y--;
			for (ll j = 0; j < n; j++) {
				col[x][j] = (col[y][j] + col[x][j]) % mod;
			}
		}
		for (ll i = 0; i < n; i++) {
			for (ll j = 0; j < m; j++) {
				ll now = 0;
				for (ll k = 0; k < n; k++) {
					now = (now + col[i][k] * arr[k][j]) % mod;
				}
				cout << now << ' ';
			}
			cout << '\n';
		}
	}
}
