#include <bits/stdc++.h>
typedef long long ll;

const ll INF = 9'000'000'000'000'000'000;

template<typename T>
bool assign_max(T& a, T b) {
	if (b > a) {
		a = b;
		return true;
	}
	return false;
}

template<typename T>
bool assign_min(T& a, T b) {
	if (b < a) {
		a = b;
		return true;
	}
	return false;
}

using namespace std;

int main() {
    	ios_base::sync_with_stdio(false);
    	cin.tie(0);
    	cout.tie(0);
	ll k, m;
	cin >> k >> m;
	vector<ll> dp(m + 1, 0);
	for (ll i = 2; i <= m; i++) {
		dp[i] = dp[i - 1] + (i - 1) * (i - 2) / 2 + (i - 1);
	}
	string ans;
	char last = 'a';
	while (k > 0) {
		ll x = upper_bound(dp.begin(), dp.end(), k) - dp.begin();
		x--;
		k -= dp[x];
		if (ans.size() != 0) {
			ans.push_back('b');
		} else {
			ans.push_back('a');
		}
		for (ll i = 1; i < x; i++) {
			ans.push_back(last);
		}
		//last++;
	}
	if (ans.size() > m) {
		return 1;
	}
	cout << ans;
}
